import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "~/components/ui/select";
import type { SelectProps } from "@radix-ui/react-select";
import type { ElementType, SVGProps } from "react";
import {MODELS, getModelData, type Model, type ModelIcon } from "~/lib/models";

interface ModelSelectorProps extends SelectProps {
  value: Model;
  onChange: (value: Model) => void;
  disabledModels?: Model[];
}

export function ModelSelector({
  value,
  onChange,
  disabledModels,
  ...props
}: ModelSelectorProps) {
  return (
    <Select value={value} onValueChange={onChange} {...props}>
      <SelectTrigger className="nodrag w-full border-none shadow-none focus:ring-0 py-0">
        <SelectValue placeholder="Select model" />
      </SelectTrigger>
      <SelectContent>
        {MODELS.map((model) => {
          const modelData = getModelData(model);
          const Icon = ModelIcons[modelData.icon];
          return (
            <SelectItem
              key={model}
              value={model}
              disabled={disabledModels?.includes(model)}
            >
              <div className="flex items-center gap-2">
                <Icon className="h-4 w-4" />
                <span className="text-muted-foreground">{model}</span>
              </div>
            </SelectItem>
          );
        })}
      </SelectContent>
    </Select>
  );
}

